INSERT INTO `refresh_field_issuese` VALUES ('496', '', '', 'Japan', 'Nikko Memorial Hospital', '341011', '44257', '', '04DS:KKI: A streak artifact occurred.', 'You can see something like a streak artifact in the trunk.\n        [Phenomenon confirmation]\n        I was able to confirm.\n        Streak-like artifacts were seen in 2 patients.\n        [Details of work]\n        A streak artifact appeared at the part where the 17th and 26th detector modules overlap.\n        I checked the RAW data, but couldn_t determine which side the artifact came from.\n        We replaced the 17th detector with a new one and swapped the 26th and 34th detectors.\n        (17⇐NEW 26⇔34)\n        After performing various calibrations, we conducted an image quality test and confirmed that there were no problems.\n        We performed a test scan and confirmed that there were no problems.\n\n12Nc: 459800911271 Name: TDMS V2 M.Det Class A FRU QTY: 1', '4', '', 'DMS Module, check defective SN\n459800911271:TDMS V2 M.Det Class A FRU QTY:1', '', '', '', '', '', '', '', '', '', '', '', '', '117116746', 'In Process', '657180', '1', 'Closed - Done', 'Closed - Done', '', 'CTUDT00632110', '', '', '', '', '', 'Engineering', '', '', '', '', '', '', 'DMS Module', '', '', '', '', '', 'Refresh', '', '', 'System issue', 'System', 'DMS-HW', 'DMS Module', 'DMS-HW-DMS Module', 'DMS-HW-DMS Module', 'CTUDT00632110', 'DMS Module', 'DMS-HW', 'DMS-HW-DMS Module', 'No', 'y', 'y', 'y', 'y', 'y', 'No', 'y', 'y', 'y', 'y', 'y', 'y', 'y', 'y', 'y', 'y', 'y', '');
